import { React, useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
//import { getDealer } from '../Web3Client';


const StartDialog=({startHandler,initializeContract,dealer,account,player,joinGameHandler,setStatusHandler,startGameHandler})=>{

const [isOpen, setIsOpen] =useState(true);
const [isReady,setIsReady]=useState(false);
const [user,setUser]=useState(null)
const nullPlayer="0x0000000000000000000000000000000000000000";


useEffect(()=>{
  if(account!==null&&dealer!==null){
    if(account.toLowerCase()===dealer.toLowerCase()&&player!==nullPlayer)
    setIsReady(true)

  else{
    setIsReady(false)
  }
  }
  else{
    setIsReady(false)
  }

},[dealer,account,player])

useEffect(()=>{
  if(account!==null&&dealer!==null){
  if(account.toLowerCase()===dealer.toLowerCase()){
    setUser(0)
  }
  if(account.toLowerCase()===player.toLowerCase()){
    setUser(1)
  }

}

},[dealer,account,player])






  return (
    <div>
      <Dialog
        open={isOpen}
        onClose={()=>{setIsOpen(false)}}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {user===1&&(<Typography>{"Welcome player"}</Typography>)}
          {user===0&&(<Typography>{"Welcome dealer"}</Typography>)}
          {user===null&&(<Typography>{"Welcome"}</Typography>)}
        </DialogTitle>
        <DialogContent>

          <DialogContentText id="alert-dialog-description" >
            {player===nullPlayer?(<div><Typography>{`Dealer is ${dealer}`}</Typography>
            <Typography>{`This is your current address: ${account}`}</Typography></div>):
            (<div><Typography>{`Dealer is ${dealer}`}</Typography>
            <Typography>{`Player is ${player}`}</Typography></div>)}


          </DialogContentText>


        {/* <TextField
          id="outlined-number"
          label="bet"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        /> */}
        </DialogContent>
        <DialogActions>
          {isReady&&<Button onClick={()=>
          {
            //startGameHandler()
            startHandler()

            }} >
            start game
          </Button>}

          {player===nullPlayer&&<Button onClick={()=>
          {
            joinGameHandler()
            }} autoFocus>
            join
          </Button>}
        </DialogActions>
      </Dialog>
    </div>
  );
}
export default StartDialog
