import Web3 from "web3"
import BlackJackContractBuild from "contracts/BlackJackV2.json"

let BlackJackContract;
let isInitial=false;

export const deploy= async (max,min)=>{

let provider = window.ethereum 

const web3 =new Web3(provider)

const networkID= await web3.eth.net.getId()

try{

    BlackJackContract= web3.eth.Contract(BlackJackContractBuild.abi,BlackJackContractBuild.networks[networkID].address).deploy({arguments: [min,max],value:max*10})
}catch(err){
console.log(err)
}



} 



export const joinGame=()=>{
    if(isInitial){
        deploy();
    }
    else{
        BlackJackContract.methods.joinGame().send()
    }


}
