          InputLabelProps={{
            shrink: true,
          }}
        /> */}
        </DialogContent>
        <DialogActions>
          {player!=null&&<Button onClick={()=>
          {
            //startHandler()

            }} >
            start game
          </Button>}

          {player==null&&<Button onClick={()=>
          {
            joinGameHandler()
            }} autoFocus>
            join
          </Button>}
        </DialogActions>
      </Dialog>
    </div>
  );
}
export default StartDialog
