import { React, useState, useEffect } from 'react';
import { CountdownCircleTimer } from "react-countdown-circle-timer";
import styles from "../style/timer.module.css";
import Avatar from '@mui/material/Avatar';

const PlayerStatus = ({ playerPosition, playerName, isStartCount, nextPlayerHandler, hitHandler, playerIndex, playerStandHandler }) => {

    const [isCount, setIsCount] = useState(false)
    const [key, setKey] = useState('0')
    const [duration,] = useState(35)

    useEffect(() => {
        if (isStartCount === true) {
            setIsCount(true)
        }


    }, [isStartCount])

    const abbreviationAvatar = (playerName) => {
        return {
            sx: {
                bgcolor: "#8c9eff",
            },
            children: playerName[0].toUpperCase(),
        };


    }
    //console.log(abbreviationAvatar(playerName))



    return (
        <div className={styles[playerPosition]}>

            <CountdownCircleTimer
                onComplete={
                    () => {
                        setIsCount(false)
                        nextPlayerHandler()
                    }
                }
                key={key}
                isPlaying={isCount}
                strokeWidth={3}
                duration={duration}
                size={46}
                colors={["#004777", "#F7B801", "#A30000", "#A30000"]}
                colorsTime={[15, 10, 5, 0]}>
                {() => <Avatar{...abbreviationAvatar(playerName)} />}
            </CountdownCircleTimer>
            <button onClick={() => {
                hitHandler(playerIndex)
                setKey((preKey) => preKey + 1)
            }}
                disabled={!isCount}
            >{"hit"}</button>

            <button onClick={() => {
                playerStandHandler()
                nextPlayerHandler()
                setKey((preKey) => preKey + 1)
                setIsCount(false)
            }}
                disabled={!isCount}
            >{"stand"}</button>
        </div>

    )

}
export default PlayerStatus
