import { React, useState, useEffect } from 'react';
import Dealer from './components/Dealer';
import Player from './components/Players';
import styles from "../src/style/table.module.css"
import backgroundImage from "./table_background.jpeg"

//import ReactDOM from 'react-dom';



const App=()=> {
  const playerList=[]
  const player1={
    bet:10,
    name:"1",
    isMe:true,
    cardList:[
      {
        value:"3",
        type:"D"
      },
      {
        value:"J",
        type:"D"
      }
    ]
  }
  const player2={
    bet:12,
    name:"2",
    isMe:false,
    cardList:[
      {
        value:"7",
        type:"D"
      },
      {
        value:"4",
        type:"H"
      }
    ]
  }
  const player3={
    bet:5,
    name:"3",
    isMe:false,
    cardList:[
      {
        value:"8",
        type:"D"
      },
      {
        value:"9",
        type:"H"
      },{
        value:"2",
        type:"D"
      },{
        value:"6",
        type:"D"
      }
    ]
  }
  const player4={
    bet:7,
    name:"4",
    isMe:false,
    cardList:[
      {
        value:"2",
        type:"D"
      },
      {
        value:"A",
        type:"H"
      },{
        value:"2",
        type:"D"
      },{
        value:"6",
        type:"D"
      }
    ]
  }
  playerList.push(player1)
  playerList.push(player2)
  playerList.push(player3)
  playerList.push(player4)

  return (
    <>
    <div 
    className={styles.table}
    style={{backgroundImage: `url(${backgroundImage})`}}
    >
    <Dealer/>
    {playerList.map((player, index)=>{
      return (<Player
      key={index}
      playerIndex={index}
      name={player.name}
      isMe={player.isMe}
      bet={player.bet}
      cardList={player.cardList}
      />)
    })}
    </div>

    </>
  )
}

export default App;
