import { React, useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
//import { getDealer } from '../Web3Client';


const StartDialog=({startHandler,initializeContract,dealer,joinGameHandler,setStatusHandler})=>{

const [isOpen, setIsOpen] =useState(true);
const [displayDealer,setDisplayDealer]=useState(null);

useEffect(()=>{
  setDisplayDealer(dealer)
},[dealer])




  return (
    <div>
      <Dialog
        open={isOpen}
        onClose={()=>{setIsOpen(false)}}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          {"Welcome player"}
        </DialogTitle>
        <DialogContent>

          <DialogContentText id="alert-dialog-description">
            {`This game is deployed by ${displayDealer}`}
            <hr></hr>
          </DialogContentText>


        {/* <TextField
          id="outlined-number"
          label="bet"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        /> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={()=>
          {
            //startHandler()

            initializeContract()
            setStatusHandler()
            }} autoFocus>
            Check status
          </Button>

          <Button onClick={()=>
          {
            joinGameHandler()

            }} autoFocus>
            join
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
export default StartDialog
